import React, { Component } from 'react';

class WishMessage extends Component{
  constructor(props){
      super(props);
      this.state ={
          message:'Choose color from below given option.'
       }
  }

    render(){
        return(
            <div>
             {this.state.message}
             <div>
                <button className="btn btn-primary" onClick={this.SayGoodMorning}>Click me</button>
            </div>
            </div>


        );
    }

    SayGoodMorning(){
        this.setState({message : 'Good Morning'})
   };

}

export default WishMessage;
