import React from 'react';

class ProductItems extends React.Component{
    constructor(props){
        super(props);

        this.state = {
           item:{
               id: '001',
               name:'samsung watch',
               qty:4,
               price:5000
           }
        }
    }

    increseQuentity = () =>{
        this.setState({
            ...this.state.item, 
            qty:this.state.item.qty+1
        })
    };

    // decreseQuentity = () =>{
    //     this.setState({
    //        ...this.state.item, 
    //         qty:this.state.item.qty-1
    //     });
    //  };

    decreseQuentity = () =>{
        this.setState(this.state,{
           item:{
               ...this.state.item,
               qty:this.state.item.qty
           }
        })
    }


    render(){
        return(<div>
            <pre>{JSON.stringify(this.state)}</pre>
            <table className="table table-hover text-center table-striped table-warning mt-3">
              <thead className="bg-dark text-white">
                  <th>Item Id</th>
                  <th>Item Name</th>
                  <th>Item Quentity</th>
                  <th>Item Price</th>
                  <th>Item Total Price</th>
              </thead>
              <tbody>
                 <tr>
                    <td>{this.state.item.id}</td>
                    <td>{this.state.item.name}</td>
                    <td>
                        <i className="fa fa-minus-circle text-primary mr-2" onClick={this.decreseQuentity}></i> 
                        {this.state.item.qty} 
                        <i className="fa fa-plus-circle text-primary ml-2" onClick={this.increseQuentity}></i>    
                    </td>

                    <td>{this.state.item.price}</td>
                    <td>{'total'}</td>
                 </tr>
              </tbody>
            </table>
        </div>);
    }
}

export default ProductItems;
